// File: blockchain/contracts/Library.sol

// SPDX-License-Identifier: MIT
// Specifies the license for your code.
pragma solidity ^0.8.19; // Tells the compiler which Solidity version to use.

// We import helpful, pre-built contracts from the OpenZeppelin library.
import "@openzeppelin/contracts/access/Ownable.sol"; // For admin functions (like adding books)
import "@openzeppelin/contracts/utils/Counters.sol"; // For creating unique IDs

// This is the main smart contract for our library.
contract Library is Ownable {
    using Counters for Counters.Counter;
    
    // A counter to give each new loan a unique ID.
    Counters.Counter private _loanIds;

    // This is the on-chain "database". It stores which book is borrowed by which user.
    // We map a book's ID (a number) to the borrower's wallet address.
    // mapping(bookId => borrowerAddress)
    mapping(uint256 => address) public borrowedBy;

    // An "event" is like a public broadcast or a log entry on the blockchain.
    // Our backend will listen for this event to know when a book has been borrowed.
    event BookBorrowed(uint256 indexed loanId, uint256 indexed bookId, address indexed borrower, uint256 dueDate);
    event BookReturned(uint256 indexed bookId, address indexed borrower);


    // --- The Borrow Function ---
    // This is the function our frontend will call when a user wants to borrow a book.
    function borrowBook(uint256 bookId) public {
        // Rule 1: The book must not already be borrowed.
        // In Solidity, an empty address slot is represented by address(0).
        require(borrowedBy[bookId] == address(0), "Book is currently unavailable.");

        // Record the loan on the blockchain
        borrowedBy[bookId] = msg.sender; // msg.sender is the wallet address of the user who called this function

        // Create a unique ID for this specific loan event
        _loanIds.increment();
        uint256 newLoanId = _loanIds.current();

        // Calculate a due date (14 days from the current time of the transaction)
        uint256 dueDate = block.timestamp + 14 days;

        // Broadcast the BookBorrowed event for our backend to hear
        emit BookBorrowed(newLoanId, bookId, msg.sender, dueDate);
    }


    // --- The Return Function ---
    // This function will be called when a user returns a book.
    function returnBook(uint256 bookId) public {
        // Rule 1: The book must have been borrowed in the first place.
        require(borrowedBy[bookId] != address(0), "This book was not borrowed.");
        
        // Rule 2: Only the person who borrowed the book can return it.
        require(borrowedBy[bookId] == msg.sender, "You are not the borrower of this book.");

        // Clear the loan record from the blockchain
        borrowedBy[bookId] = address(0);

        // Broadcast the BookReturned event
        emit BookReturned(bookId, msg.sender);
    }
}

